import React from 'react'
import styled from 'styled-components'
import PropTypes from 'prop-types'

function Button({ name, icon, onClick, bg, bPad, color, bRad }) {
    return (
        <ButtonStyled
            onClick={onClick}
            bg={bg}
            bPad={bPad}
            color={color}
            bRad={bRad}
            aria-label={name}  // Adding accessibility label
        >
            {icon}
            {name}
        </ButtonStyled>
    )
}

const ButtonStyled = styled.button`
    outline: none;
    border: none;
    font-family: inherit;
    font-size: inherit;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
    transition: all 0.4s ease-in-out;
    background: ${({ bg }) => bg || 'transparent'};  // Default to transparent
    padding: ${({ bPad }) => bPad || '0.5rem 1rem'};  // Default padding
    border-radius: ${({ bRad }) => bRad || '4px'};  // Default border radius
    color: ${({ color }) => color || '#000'};  // Default color

    &:hover {
        opacity: 0.8;
    }
`;

Button.propTypes = {
    name: PropTypes.string.isRequired,
    icon: PropTypes.node,
    onClick: PropTypes.func,
    bg: PropTypes.string,
    bPad: PropTypes.string,
    color: PropTypes.string,
    bRad: PropTypes.string,
};

Button.defaultProps = {
    bg: 'transparent',
    bPad: '0.5rem 1rem',
    color: '#000',
    bRad: '4px',
};

export default Button;
